import React, { useRef } from 'react';
import styled from 'styled-components';

interface AuctionCardProps {
  title: string;
  description: string;
  price: number;
  imageUrl?: string;
  onClick?: () => void;
}

const getRandomRotation = () => {
  // Random rotation between -3deg and +3deg
  return (Math.random() * 6 - 3).toFixed(2) + 'deg';
};

const AuctionCard: React.FC<AuctionCardProps> = ({ title, description, price, imageUrl, onClick }) => {
  const rotationRef = useRef(getRandomRotation());
  return (
    <StyledWrapper rotation={rotationRef.current}>
      <div className="card">
        {imageUrl ? (
          <img src={imageUrl} alt={title} className="img" />
        ) : (
          <div className="no-image">
            <span className="no-image-text">No Image</span>
          </div>
        )}
        <div className="infoBox">
          <p className="head">{title}</p>
          <p className="price">${price.toLocaleString(undefined, { minimumFractionDigits: 2 })}</p>
        </div>
        <div className="descBox">
          <p className="description">{description}</p>
        </div>
      </div>
    </StyledWrapper>
  );
};

const StyledWrapper = styled.div<{ rotation: string }>`
  .card {
    width: 320px;
    height: 440px;
    background: rgba(40, 40, 40, 0.82);
    border-radius: 20px;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: flex-end;
    color: white;
    transition: 0.2s ease-in-out;
    position: relative;
    cursor: pointer;
    overflow: hidden;
    box-shadow: 0 4px 24px 0 rgba(30,30,30,0.12);
    margin: 0 auto;
    margin-bottom: 2rem;
    will-change: transform;
  }
  .img, .no-image {
    height: 60%;
    width: 100%;
    object-fit: cover;
    position: absolute;
    top: 0;
    left: 0;
    transition: 0.3s filter, 0.2s transform;
    z-index: 1;
    border-top-left-radius: 20px;
    border-top-right-radius: 20px;
    display: flex;
    align-items: center;
    justify-content: center;
  }
  .no-image {
    background: #111;
  }
  .no-image-text {
    color: #fff;
    font-size: 1.1em;
    opacity: 0.7;
    letter-spacing: 1px;
  }
  .infoBox {
    position: absolute;
    bottom: 0;
    left: 0;
    width: 100%;
    background: rgba(40, 40, 40, 0.82);
    padding: 1.1em 1em 0.7em 1em;
    z-index: 2;
    display: flex;
    flex-direction: column;
    align-items: flex-start;
    border-bottom-left-radius: 20px;
    border-bottom-right-radius: 20px;
    transition: background 0.2s;
  }
  .head {
    font-size: 1.25em;
    font-weight: bold;
    margin-bottom: 0.2em;
  }
  .price {
    font-size: 1.15em;
    font-weight: bold;
    color: #ffb347;
    margin-bottom: 0;
  }
  .descBox {
    position: absolute;
    left: 0;
    bottom: 0;
    width: 100%;
    background: rgba(40, 40, 40, 0.98);
    padding: 1.1em 1em 0.7em 1em;
    z-index: 3;
    opacity: 0;
    max-height: 0;
    overflow: hidden;
    transition: opacity 0.25s, max-height 0.25s;
    border-bottom-left-radius: 20px;
    border-bottom-right-radius: 20px;
  }
  .description {
    font-size: 1.1em;
    color: #e0e0e0;
    font-weight: 400;
    margin: 0;
  }
  .card:hover .descBox {
    opacity: 1;
    max-height: 180px;
  }
  .card:hover .infoBox {
    background: rgba(40, 40, 40, 0.98);
  }
  .card:hover .img, .card:hover .no-image {
    filter: blur(4px) brightness(0.9);
    transform: rotate(${props => props.rotation});
  }
`;

export default AuctionCard;
