import React, { useEffect, useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { supabase } from '../utils/supabaseClient';
import { Product, Purchase } from '../types';
import { useNavigate } from 'react-router-dom';

const UserProfile: React.FC = () => {
  const { user, signOut } = useAuth();
  const navigate = useNavigate();
  const [myProducts, setMyProducts] = useState<Product[]>([]);
  const [myPurchases, setMyPurchases] = useState<Purchase[]>([]);
  const [notifications, setNotifications] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchUserData = async () => {
      if (!user) return;
      try {
        // Fetch user's active auctions (not ended or sold)
        const { data: products, error: productsError } = await supabase
          .from('products')
          .select('*')
          .eq('seller_id', user.id)
          .not('status', 'in', '(ended,sold)')
          .order('created_at', { ascending: false });
        if (productsError) throw productsError;
        setMyProducts(products || []);

        // Fetch user's purchased items
        const { data: purchases, error: purchasesError } = await supabase
          .from('purchases')
          .select('*')
          .eq('buyer_id', user.id)
          .order('purchase_date', { ascending: false });
        if (purchasesError) throw purchasesError;
        setMyPurchases(purchases || []);

        // Fetch ended/sold auctions where user placed a bid but did not win
        const { data: lostProducts } = await supabase
          .from('products')
          .select('id, title, status')
          .in('status', ['ended', 'sold'])
          .neq('seller_id', user.id);
        let lostNotifications: any[] = [];
        if (lostProducts && lostProducts.length > 0) {
          // For each ended product, check if user placed a bid but did not win
          for (const product of lostProducts) {
            const { data: topBid } = await supabase
              .from('bids')
              .select('*')
              .eq('product_id', product.id)
              .order('bid_amount', { ascending: false })
              .limit(1)
              .single();
            if (topBid && topBid.user_id !== user.id) {
              // Check if user placed any bid
              const { count } = await supabase
                .from('bids')
                .select('*', { count: 'exact', head: true })
                .eq('product_id', product.id)
                .eq('user_id', user.id);
              if (count && count > 0) {
                lostNotifications.push({
                  type: 'lost',
                  productTitle: product.title,
                  productId: product.id
                });
              }
            }
          }
        }
        // Fetch purchases with pending payment
        const pendingPurchases = (purchases || []).filter(p => p.payment_status === 'pending');
        const wonNotifications = pendingPurchases.map(p => ({
          type: 'won',
          productTitle: p.product_title,
          productId: p.product_id,
          purchaseId: p.id
        }));
        setNotifications([...wonNotifications, ...lostNotifications]);
      } catch (error) {
        console.error('Error fetching user data:', error);
      } finally {
        setLoading(false);
      }
    };
    fetchUserData();
  }, [user]);

  const handleSignOut = async () => {
    try {
      await signOut();
    } catch (error) {
      console.error('Error signing out:', error);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-indigo-500"></div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8 bg-gray-100 text-gray-900 min-h-screen">
      <div className="max-w-4xl mx-auto bg-white rounded-lg shadow-md p-6">
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-3xl font-bold text-gray-900">My Profile</h1>
          <button
            onClick={handleSignOut}
            className="px-4 py-2 border rounded-md shadow-sm text-sm font-medium text-white bg-red-600 hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500"
          >
            Sign Out
          </button>
        </div>

        {/* Notifications Section */}
        <div className="mb-8">
          <h2 className="text-xl font-semibold text-gray-900 mb-4 flex items-center">
            Notifications
            {notifications.length > 0 && (
              <span className="ml-2 inline-block w-3 h-3 bg-orange-500 rounded-full"></span>
            )}
          </h2>
          {notifications.length === 0 ? (
            <p className="text-gray-600">No new notifications.</p>
          ) : (
            <ul className="space-y-2">
              {notifications.map((notif, idx) => (
                <li key={idx} className="bg-gray-100 rounded p-3 flex items-center justify-between">
                  {notif.type === 'won' ? (
                    <>
                      <span className="text-green-700 font-semibold">You won: {notif.productTitle}</span>
                      <button
                        className="ml-4 px-3 py-1 bg-green-600 text-white rounded hover:bg-green-700"
                        onClick={() => navigate(`/payment/${notif.productId}`)}
                      >
                        Pay Now
                      </button>
                    </>
                  ) : (
                    <span className="text-red-700 font-semibold">You lost: {notif.productTitle}</span>
                  )}
                </li>
              ))}
            </ul>
          )}
        </div>

        {/* My Auctions Section */}
        <div className="mb-8">
          <h2 className="text-xl font-semibold text-gray-900 mb-4">My Active Auctions</h2>
          {myProducts.length > 0 ? (
            <div className="space-y-4">
              {myProducts.map((product) => (
                <div key={product.id} className="bg-gray-100 rounded-lg shadow-md p-4">
                  <h3 className="text-lg font-medium text-gray-900">{product.title}</h3>
                  <p className="text-sm text-gray-600 mt-1">
                    Current Price: ${product.current_price.toFixed(2)}
                  </p>
                  <p className="text-sm text-gray-600">
                    Status: {product.status}
                  </p>
                  <p className="text-sm text-gray-600">
                    Ends: {new Date(product.end_time).toLocaleString()}
                  </p>
                </div>
              ))}
            </div>
          ) : (
            <p className="text-gray-600">You have no active auctions.</p>
          )}
        </div>

        {/* Purchased Items Section */}
        <div className="mt-8">
          <h2 className="text-xl font-semibold text-gray-900 mb-4">Items You Purchased</h2>
          {myPurchases.length > 0 ? (
            <div className="space-y-4">
              {myPurchases.map((purchase) => (
                <div key={purchase.id} className="bg-gray-100 rounded-lg shadow-md p-4">
                  <div className="flex items-center space-x-4">
                    {purchase.product_image_url ? (
                      <img
                        src={purchase.product_image_url}
                        alt={purchase.product_title}
                        className="w-16 h-16 object-cover rounded-md"
                      />
                    ) : (
                      <div className="w-16 h-16 bg-gray-300 rounded-md flex items-center justify-center">
                        <span className="text-gray-500 text-sm">No image</span>
                      </div>
                    )}
                    <div className="flex-1">
                      <h3 className="text-lg font-medium text-gray-900">{purchase.product_title}</h3>
                      <p className="text-sm text-gray-600">
                        Purchase Date: {new Date(purchase.purchase_date).toLocaleDateString()}
                      </p>
                      <p className="text-sm text-gray-600">
                        Amount Paid: <span className="font-semibold text-green-600">${purchase.winning_bid_amount.toFixed(2)}</span>
                      </p>
                      <p className="text-sm text-gray-600">
                        Status: <span className={`font-semibold ${
                          purchase.payment_status === 'completed' ? 'text-green-600' : 
                          purchase.payment_status === 'pending' ? 'text-yellow-600' : 'text-red-600'
                        }`}>
                          {purchase.payment_status.charAt(0).toUpperCase() + purchase.payment_status.slice(1)}
                        </span>
                        {purchase.payment_status === 'pending' && (
                          <button
                            className="ml-4 px-3 py-1 bg-green-600 text-white rounded hover:bg-green-700"
                            onClick={() => navigate(`/payment/${purchase.product_id}`)}
                          >
                            Pay Now
                          </button>
                        )}
                      </p>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <p className="text-gray-600">You haven't purchased any items yet.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
