import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { supabase } from '../utils/supabaseClient';
import { v4 as uuidv4 } from 'uuid';

const CreateProduct: React.FC = () => {
  const navigate = useNavigate();
  const { user } = useAuth();
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    starting_price: '',
    end_time: '',
  });
  const [imageFile, setImageFile] = useState<File | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [suggestedDescription, setSuggestedDescription] = useState('');

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleImageChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files[0]) {
      setImageFile(e.target.files[0]);
    } else {
      setImageFile(null);
    }
  };

  const handleTitleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    setFormData((prev) => ({ ...prev, title: value }));
    if (value.length > 0) {
      setSuggestedDescription(`Introducing "${value}" – a unique item now available for auction!`);
    } else {
      setSuggestedDescription('');
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!user) return;

    setLoading(true);
    setError('');

    let imageUrl: string | null = null;

    try {
      // Upload image to Supabase Storage if a file is selected
      if (imageFile) {
        const fileExt = imageFile.name.split('.').pop();
        const fileName = `${uuidv4()}.${fileExt}`;
        const filePath = `product-images/${fileName}`;

        const { error: uploadError } = await supabase.storage
          .from('product-images') // Ensure you have a bucket named 'product-images' in Supabase Storage
          .upload(filePath, imageFile);

        if (uploadError) throw uploadError;

        // Get the public URL of the uploaded image
        const { data: publicUrlData } = supabase.storage
          .from('product-images')
          .getPublicUrl(filePath);

        imageUrl = publicUrlData.publicUrl;
      }

      // Insert product data into the database
      const { error: insertError } = await supabase.from('products').insert([
        {
          title: formData.title,
          description: formData.description,
          starting_price: parseFloat(formData.starting_price),
          current_price: parseFloat(formData.starting_price),
          end_time: new Date(formData.end_time).toISOString(),
          seller_id: user.id,
          status: 'active',
          image_url: imageUrl, // Store the image URL
        },
      ]);

      if (insertError) throw insertError;

      navigate('/');
    } catch (error: any) {
      console.error('Error creating auction:', error);
      setError(error.message || 'Failed to create auction');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="px-4 py-8 min-h-screen">
      <div className="max-w-2xl mx-auto bg-[rgba(24,25,27,0.92)] border-2 border-[#18191b] rounded-2xl shadow-lg p-8">
        <h1 className="text-4xl font-extrabold text-white text-center mb-8" style={{ textShadow: '2px 2px 6px #000, 0 0 2px #000' }}>
          Create New Auction
        </h1>

        <form onSubmit={handleSubmit} className="space-y-6">
          {error && (
            <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative" role="alert">
              <span className="block sm:inline">{error}</span>
            </div>
          )}

          <div>
            <label htmlFor="title" className="block text-lg font-semibold text-white mb-2">
              Title
            </label>
            <input
              type="text"
              id="title"
              name="title"
              required
              value={formData.title}
              onChange={handleTitleChange}
              className="mt-1 block w-full rounded-md border border-[#444] bg-[#232323] text-white placeholder-gray-400 shadow-sm focus:border-orange-500 focus:ring-orange-500 text-lg py-3 px-4"
              placeholder="Enter auction title"
            />
          </div>

          <div>
            <label htmlFor="description" className="block text-lg font-semibold text-white mb-2">
              Description
            </label>
            <textarea
              id="description"
              name="description"
              rows={4}
              required
              value={formData.description}
              onChange={handleChange}
              className="mt-1 block w-full rounded-md border border-[#444] bg-[#232323] text-white placeholder-gray-400 shadow-sm focus:border-orange-500 focus:ring-orange-500 text-lg py-3 px-4"
              placeholder="Enter auction description"
            />
            {suggestedDescription && (
              <div className="mt-2 text-sm text-orange-300 cursor-pointer hover:underline" onClick={() => setFormData((prev) => ({ ...prev, description: suggestedDescription }))}>
                Suggestion: {suggestedDescription}
              </div>
            )}
          </div>

          <div>
            <label htmlFor="starting_price" className="block text-lg font-semibold text-white mb-2">
              Starting Price ($)
            </label>
            <input
              type="number"
              id="starting_price"
              name="starting_price"
              min="0"
              step="0.01"
              required
              value={formData.starting_price}
              onChange={handleChange}
              className="mt-1 block w-full rounded-md border border-[#444] bg-[#232323] text-white placeholder-gray-400 shadow-sm focus:border-orange-500 focus:ring-orange-500 text-lg py-3 px-4"
              placeholder="Enter starting price"
            />
          </div>

          <div>
            <label htmlFor="end_time" className="block text-lg font-semibold text-white mb-2">
              Auction End Time
            </label>
            <input
              type="datetime-local"
              id="end_time"
              name="end_time"
              required
              value={formData.end_time}
              onChange={handleChange}
              min={new Date().toISOString().slice(0, 16)}
              className="mt-1 block w-full rounded-md border border-[#444] bg-[#232323] text-white placeholder-gray-400 shadow-sm focus:border-orange-500 focus:ring-orange-500 text-lg py-3 px-4"
              placeholder="Select end time"
            />
          </div>

          <div>
            <label htmlFor="image" className="block text-lg font-semibold text-white mb-2">
              Image
            </label>
            <input
              type="file"
              id="image"
              name="image"
              accept="image/*"
              onChange={handleImageChange}
              className="mt-1 block w-full text-lg text-white file:mr-4 file:py-3 file:px-6 file:rounded-full file:border-0 file:text-lg file:font-semibold file:bg-orange-50 file:text-orange-700 hover:file:bg-orange-100"
            />
            {imageFile && <p className="mt-2 text-sm text-gray-300">Selected file: {imageFile.name}</p>}
          </div>

          <div className="flex justify-end space-x-4 mt-8">
            <button
              type="button"
              onClick={() => navigate('/')}
              className="button bg-[#232323] text-white border border-[#444] hover:bg-[#18191b]"
            >
              Cancel
            </button>
            <button
              type="submit"
              disabled={loading}
              className="button button-orange"
            >
              {loading ? 'Creating...' : 'Create Auction'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default CreateProduct;
