import React from 'react';
import styled from 'styled-components';

interface AuctionCardProps {
  title: string;
  description: string;
  price: number;
  imageUrl?: string;
  onClick?: () => void;
}

const AuctionCard: React.FC<AuctionCardProps> = ({ title, description, price, imageUrl, onClick }) => {
  return (
    <StyledWrapper onClick={onClick}>
      <div className="card">
        {imageUrl ? (
          <img src={imageUrl} alt={title} className="img" />
        ) : (
          <div className="no-image">
            <span className="no-image-text">No Image</span>
          </div>
        )}
        <div className="textBox">
          <p className="head">{title}</p>
          <p className="discription">{description}</p>
          <p className="price">${price.toLocaleString(undefined, { minimumFractionDigits: 2 })}</p>
        </div>
      </div>
    </StyledWrapper>
  );
};

const StyledWrapper = styled.div`
  .card {
    width: 240px;
    height: 340px;
    background: rgba(40, 40, 40, 0.92);
    border-radius: 20px;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    color: white;
    transition: 0.2s ease-in-out;
    position: relative;
    cursor: pointer;
    overflow: hidden;
    box-shadow: 0 4px 24px 0 rgba(30,30,30,0.12);
    margin: 0 auto;
    margin-bottom: 1.5rem;
    padding: 1.2rem 1rem;
  }
  .img {
    height: 60%;
    width: 100%;
    object-fit: cover;
    position: absolute;
    top: 0;
    left: 0;
    transition: 0.2s ease-in-out;
    z-index: 1;
    border-top-left-radius: 20px;
    border-top-right-radius: 20px;
  }
  .no-image {
    height: 60%;
    width: 100%;
    background: #111;
    display: flex;
    align-items: center;
    justify-content: center;
    position: absolute;
    top: 0;
    left: 0;
    border-top-left-radius: 20px;
    border-top-right-radius: 20px;
    z-index: 1;
  }
  .no-image-text {
    color: #fff;
    font-size: 1.2em;
    opacity: 0.7;
    letter-spacing: 1px;
  }
  .textBox {
    opacity: 0;
    display: flex;
    flex-direction: column;
    align-items: start;
    justify-content: end;
    width: 100%;
    height: 100%;
    gap: 0em;
    padding: 1.2em;
    z-index: 5;
    transition: 0.2s ease-in-out;
    position: relative;
  }
  .textBox > .head {
    font-size: 1.2em;
    font-weight: bold;
  }
  .textBox > .price {
    font-size: 1.2em;
    font-weight: bold;
  }
  .textBox > .discription {
    font-size: 1em;
    color: #e0e0e0;
    font-weight: 400;
  }
  .card:hover > .textBox {
    opacity: 1;
    gap: 0.5em;
  }
  .card:hover > .img, .card:hover > .no-image {
    filter: blur(3px);
    animation: anim 3s infinite ease-in-out;
  }
  @keyframes anim {
    50% {
      transform: translateY(-10%) translateY(-5%) rotate(5deg) ;
    }
  }
  .card:hover {
    transform: scale(1.04);
  }
`;

export default AuctionCard;
