import React from 'react';
import styled from 'styled-components';

interface AuctionCardProps {
  title: string;
  description: string;
  price: number;
  imageUrl?: string;
  onClick?: () => void;
}

const AuctionCard: React.FC<AuctionCardProps> = ({ title, description, price, imageUrl, onClick }) => {
  return (
    <StyledWrapper onClick={onClick}>
      <div className="card">
        {imageUrl ? (
          <img src={imageUrl} alt={title} className="img" />
        ) : (
          <svg xmlnsXlink="http://www.w3.org/1999/xlink" viewBox="0 0 784.37 1277.39" clipRule="evenodd" fillRule="evenodd" imageRendering="optimizeQuality" textRendering="geometricPrecision" shapeRendering="geometricPrecision" version="1.1" height="100%" width="100%" xmlSpace="preserve" xmlns="http://www.w3.org/2000/svg" className="img">
            <g id="Layer_x0020_1">
              <metadata id="CorelCorpID_0Corel-Layer" />
              <g id="_1421394342400">
                <g>
                  <polygon points="392.07,0 383.5,29.11 383.5,873.74 392.07,882.29 784.13,650.54" fillRule="nonzero" fill="#343434" />
                  <polygon points="392.07,0 -0,650.54 392.07,882.29 392.07,472.33" fillRule="nonzero" fill="#8C8C8C" />
                  <polygon points="392.07,956.52 387.24,962.41 387.24,1263.28 392.07,1277.38 784.37,724.89" fillRule="nonzero" fill="#3C3C3B" />
                  <polygon points="392.07,1277.38 392.07,956.52 -0,724.89" fillRule="nonzero" fill="#8C8C8C" />
                  <polygon points="392.07,882.29 784.13,650.54 392.07,472.33" fillRule="nonzero" fill="#141414" />
                  <polygon points="0,650.54 392.07,882.29 392.07,472.33" fillRule="nonzero" fill="#393939" />
                </g>
              </g>
            </g>
          </svg>
        )}
        <div className="textBox">
          <p className="head">{title}</p>
          <p className="discription">{description}</p>
          <p className="price">${price.toLocaleString(undefined, { minimumFractionDigits: 2 })}</p>
        </div>
      </div>
    </StyledWrapper>
  );
};

const StyledWrapper = styled.div`
  .card {
    width: 220px;
    height: 320px;
    background: rgba(40, 40, 40, 0.92);
    border-radius: 20px;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    color: white;
    transition: 0.2s ease-in-out;
    position: relative;
    cursor: pointer;
    overflow: hidden;
    box-shadow: 0 4px 24px 0 rgba(30,30,30,0.12);
    margin: 0 auto;
  }
  .img {
    height: 60%;
    width: 100%;
    object-fit: cover;
    position: absolute;
    top: 0;
    left: 0;
    transition: 0.2s ease-in-out;
    z-index: 1;
    border-top-left-radius: 20px;
    border-top-right-radius: 20px;
  }
  .textBox {
    opacity: 0;
    display: flex;
    flex-direction: column;
    align-items: start;
    justify-content: end;
    width: 100%;
    height: 100%;
    gap: 0em;
    padding: 1em;
    z-index: 5;
    transition: 0.2s ease-in-out;
    position: relative;
  }
  .textBox > .head {
    font-size: 1.1em;
    font-weight: bold;
  }
  .textBox > .price {
    font-size: 1.1em;
    font-weight: bold;
  }
  .textBox > .discription {
    font-size: 0.9em;
    color: #e0e0e0;
    font-weight: 400;
  }
  .card:hover > .textBox {
    opacity: 1;
    gap: 0.5em;
  }
  .card:hover > .img {
    filter: blur(3px);
    animation: anim 3s infinite ease-in-out;
  }
  @keyframes anim {
    50% {
      transform: translateY(-10%) rotate(5deg);
    }
  }
  .card:hover {
    transform: scale(1.04);
  }
`;

export default AuctionCard;
